// diesel requires us to maintain two copies of everything:
// * the database schema description in schema.rs, automatically generated by diesel-cli
// * plus this file with matching rust structs

#[derive(Queryable)]
pub struct PlantType {
    pub id: Option<i32>,
    pub name: String,
    pub latin_name: Option<String>,
}

#[derive(Queryable)]
pub struct BasePlant {
    pub id: Option<i32>,
    pub name: String,
    pub type_: String,
    pub description: Option<String>,
    pub patent: Option<String>,
    pub relative_harvest: Option<String>,
    pub harvest_start: Option<i32>,
    pub harvest_end: Option<i32>,
    pub harvest_time_reference: Option<String>,
}

#[derive(Queryable)]
pub struct CollectionItems {
    pub id: Option<i32>,
    pub collection_id: String,
    pub notes: Option<String>,
    pub name: String,
    pub type_: String,
    pub description: Option<String>,
    pub patent: Option<String>,
    pub relative_harvest: Option<String>,
    pub harvest_start: Option<i32>,
    pub harvest_end: Option<i32>,
    pub harvest_time_reference: Option<String>,
}

#[derive(Queryable)]
pub struct Collections {
    pub id: Option<i32>,
    pub user_id: i32,
    pub name: String,
}

#[derive(Queryable)]
pub struct Users {
    pub id: Option<i32>,
    pub name: String,
}
